mapper.select:
	select answer_no, question_no, answer_title, answer_content, answer_reg_date, answer_recommend, clz_domain, mem_id
	from tbl_answer 
	
	
	question_no = #{no} and clz_domain = #{domain}
	
	
	order by answer_recommend desc, answer_no desc;

		
		select count(answer_no)
		from tbl_answer

		
		
		clz_domain = #{domain} and question_no = #{no}
