mapper.select:
select * from tbl_course c, tbl_member m where c.mem_id = m.mem_id and c.clz_domain = #{domain}
and m.mem_id like CONCAT('%', #{cri.keyword}, '%')
and m.mem_name like CONCAT('%', #{cri.keyword}, '%')
order by c.mem_id limit #{cri.first}, #{cri.perPageNum};

select count(c.mem_id) from tbl_course c, tbl_member m where c.mem_id = m.mem_id and c.clz_domain = #{domain}
and m.mem_id like CONCAT('%', #{cri.keyword}, '%')
and m.mem_name like CONCAT('%', #{cri.keyword}, '%')
