mapper.select:
set @rownum=0; select @rownum:=@rownum+1 as question_no, question_title, question_content, mem_id, case when question_reg_date < date_add(now(), interval -10 hour) then date_format(question_reg_date,"%m-%d") else date_format(question_reg_date,"%H:%i") end as question_reg_date from tbl_question
${key} like CONCAT('%',#{search.searchValue},'%') question_title like CONCAT('%',#{search.searchValue},'%') OR question_content like CONCAT('%',#{search.searchValue},'%') #{search.searchKey} like CONCAT('%',#{search.searchValue},'%')
order by question_no desc limit #{cri.first}, #{cri.perPageNum}

		select count(question_no) from tbl_question where clz_domain like
		"bit70";

		
			
				${key} like concat('%',#{searchValue},'%')
